#ifndef _REPLAYER_H
#define _REPLAYER_H

#define MAX_ARGS 5

typedef struct timeval_s {
  unsigned long secs;
  unsigned long usecs;
} timeval_t;

typedef struct s_timeval_s {
  long secs;
  long usecs;
} s_timeval_t;

typedef unsigned short op_t;
	 
struct replay_command {
	op_t command;
	timeval_t time;
	int args[MAX_ARGS];
	int ret;
	int expected_retval;
};

struct replay_workload {
        struct replay_command* srcmdp;
        unsigned int cmd_num;
        unsigned int cur_cmd;
};

#endif /* _REPLAYER_H */
